import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { BookOpen, TrendingUp, Users, Plus } from 'lucide-react';
import axios from 'axios';
import './DashboardPage.css';

const DashboardPage = () => {
  const { user, isAuthenticated } = useAuth();
  const [content, setContent] = useState([]);
  const [stats, setStats] = useState({
    totalContent: 0,
    totalSummaries: 0,
    followerCount: 0,
    followingCount: 0
  });
  const [loading, setLoading] = useState(true);

  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001/api';

  useEffect(() => {
    const loadDashboardData = async () => {
      if (isAuthenticated) {
        await fetchUserContent();
        await fetchUserStats();
      }
    };

    loadDashboardData();
  }, [isAuthenticated]); // eslint-disable-line react-hooks/exhaustive-deps

  const fetchUserContent = async () => {
    try {
      const response = await axios.get(`${API_URL}/content`);
      setContent(response.data.content || []);
    } catch (error) {
      console.error('Error fetching content:', error);
    }
  };

  const fetchUserStats = async () => {
    try {
      // Since we have user stats in the user object, we can use those
      if (user) {
        setStats({
          totalContent: user.stats?.totalContent || 0,
          totalSummaries: user.stats?.totalSummaries || 0,
          followerCount: user.followerCount || 0,
          followingCount: user.followingCount || 0
        });
      }
    } catch (error) {
      console.error('Error fetching stats:', error);
    } finally {
      setLoading(false);
    }
  };

  if (!isAuthenticated) {
    return (
      <div className="dashboard-page">
        <div className="auth-required">
          <h2>Please log in to access your dashboard</h2>
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="dashboard-page">
        <div className="loading">Loading your dashboard...</div>
      </div>
    );
  }

  return (
    <div className="dashboard-page">
      <div className="dashboard-container">
        {/* Header */}
        <div className="dashboard-header">
          <div className="header-content">
            <h1>Welcome back, {user?.fullName || user?.username}!</h1>
            <p>Here's what's happening with your content</p>
          </div>
          <div className="header-actions">
            <div className="install-extension">
              <Plus size={20} />
              <span>Install Chrome Extension to start adding content</span>
            </div>
          </div>
        </div>

        {/* Stats Cards */}
        <div className="stats-grid">
          <div className="stat-card">
            <BookOpen className="stat-icon" />
            <div className="stat-info">
              <h3>{stats.totalContent}</h3>
              <p>Total Content</p>
            </div>
          </div>

          <div className="stat-card">
            <TrendingUp className="stat-icon" />
            <div className="stat-info">
              <h3>{stats.totalSummaries}</h3>
              <p>Summaries Generated</p>
            </div>
          </div>

          <div className="stat-card">
            <Users className="stat-icon" />
            <div className="stat-info">
              <h3>{stats.followerCount}</h3>
              <p>Followers</p>
            </div>
          </div>

          <div className="stat-card">
            <Users className="stat-icon" />
            <div className="stat-info">
              <h3>{stats.followingCount}</h3>
              <p>Following</p>
            </div>
          </div>
        </div>

        {/* Recent Content */}
        <div className="dashboard-section">
          <h2>Recent Content</h2>
          {content.length > 0 ? (
            <div className="content-grid">
              {content.slice(0, 6).map((item) => (
                <div key={item._id} className="content-card">
                  <div className="content-header">
                    <h3>{item.title}</h3>
                    <span className="content-source">{item.siteName}</span>
                  </div>
                  <div className="content-preview">
                    {item.summary && typeof item.summary === 'string' ? (
                      <p>{item.summary.substring(0, 150)}...</p>
                    ) : item.content && typeof item.content === 'string' ? (
                      <p>{item.content.substring(0, 150)}...</p>
                    ) : (
                      <p>No preview available</p>
                    )}
                  </div>
                  <div className="content-footer">
                    <span className="content-date">
                      {new Date(item.createdAt).toLocaleDateString()}
                    </span>
                    <a 
                      href={item.url} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="content-link"
                    >
                      View Original
                    </a>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="empty-state">
              <BookOpen size={48} />
              <h3>No content yet</h3>
              <p>Install our Chrome extension and start extracting content from your favorite websites!</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default DashboardPage;
