import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { User, LogOut, Home, Compass, BarChart3 } from 'lucide-react';
import './Navigation.css';

const Navigation = () => {
  const { user, logout, isAuthenticated } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <nav className="navigation">
      <div className="nav-container">
        <Link to="/" className="nav-brand">
          <BarChart3 size={24} />
          <span>Content Summarizer</span>
        </Link>

        <div className="nav-links">
          <Link to="/" className="nav-link">
            <Home size={18} />
            <span>Home</span>
          </Link>

          <Link to="/explore" className="nav-link">
            <Compass size={18} />
            <span>Explore</span>
          </Link>

          {isAuthenticated ? (
            <>
              <Link to="/dashboard" className="nav-link">
                <User size={18} />
                <span>Dashboard</span>
              </Link>

              <div className="nav-user">
                <span className="nav-username">Hi, {user?.username}</span>
                <button onClick={handleLogout} className="nav-logout">
                  <LogOut size={16} />
                  <span>Logout</span>
                </button>
              </div>
            </>
          ) : (
            <div className="nav-auth">
              <Link to="/login" className="nav-link-auth">Login</Link>
              <Link to="/register" className="nav-link-auth nav-link-primary">Sign Up</Link>
            </div>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navigation;
