import React from 'react';
import { User, BookOpen, Users } from 'lucide-react';

const UserProfilePage = () => {
  return (
    <div style={{ padding: '40px 20px', textAlign: 'center' }}>
      <div style={{ maxWidth: '600px', margin: '0 auto' }}>
        <User size={64} style={{ color: '#667eea', marginBottom: '20px' }} />
        <h1>User Profile</h1>
        <p style={{ color: '#666', fontSize: '18px', lineHeight: '1.6' }}>
          This page will show user profiles, their public content, followers, and following.
          Coming soon in the next phase of development!
        </p>
        <div style={{ 
          display: 'grid', 
          gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))', 
          gap: '20px', 
          marginTop: '40px' 
        }}>
          <div style={{ 
            padding: '20px', 
            background: '#f8f9fa', 
            borderRadius: '8px' 
          }}>
            <BookOpen size={32} style={{ color: '#667eea', marginBottom: '10px' }} />
            <h3>Public Content</h3>
            <p style={{ color: '#666', fontSize: '14px' }}>View user's shared content</p>
          </div>
          <div style={{ 
            padding: '20px', 
            background: '#f8f9fa', 
            borderRadius: '8px' 
          }}>
            <Users size={32} style={{ color: '#667eea', marginBottom: '10px' }} />
            <h3>Social Features</h3>
            <p style={{ color: '#666', fontSize: '14px' }}>Follow and be followed</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserProfilePage;
