import React, { useState, useEffect } from 'react';
import { Search, UserPlus, UserCheck, Clock, Eye, Users, FileText } from 'lucide-react';
import './SearchPage.css';

const SearchPage = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [followRequests, setFollowRequests] = useState([]);
  const [followingStatus, setFollowingStatus] = useState({});

  // Fetch pending follow requests
  useEffect(() => {
    fetchFollowRequests();
  }, []);

  const fetchFollowRequests = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('/api/users/me/requests', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const data = await response.json();
        setFollowRequests(data.requests);
      }
    } catch (error) {
      console.error('Failed to fetch follow requests:', error);
    }
  };

  const handleSearch = async (query) => {
    if (!query.trim() || query.length < 2) {
      setSearchResults([]);
      return;
    }

    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`/api/users/search?q=${encodeURIComponent(query)}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const data = await response.json();
        setSearchResults(data.users);

        // Get follow status for each user
        const statusPromises = data.users.map(async (searchUser) => {
          try {
            const userResponse = await fetch(`/api/users/${searchUser.username}`, {
              headers: {
                'Authorization': `Bearer ${token}`,
                'Content-Type': 'application/json'
              }
            });

            if (userResponse.ok) {
              const userData = await userResponse.json();
              return {
                userId: searchUser._id,
                isFollowing: userData.user.isFollowing,
                hasPendingRequest: userData.user.hasPendingRequest
              };
            }
          } catch (error) {
            console.error('Failed to get user status:', error);
          }
          return { userId: searchUser._id, isFollowing: false, hasPendingRequest: false };
        });

        const statuses = await Promise.all(statusPromises);
        const statusMap = {};
        statuses.forEach(status => {
          statusMap[status.userId] = status;
        });
        setFollowingStatus(statusMap);
      }
    } catch (error) {
      console.error('Search failed:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleFollowToggle = async (username, userId) => {
    try {
      const token = localStorage.getItem('token');
      const currentStatus = followingStatus[userId];

      const endpoint = currentStatus?.isFollowing 
        ? `/api/users/${username}/unfollow`
        : `/api/users/${username}/follow`;

      const response = await fetch(endpoint, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const data = await response.json();

        // Update following status
        setFollowingStatus(prev => ({
          ...prev,
          [userId]: {
            isFollowing: data.status === 'following',
            hasPendingRequest: data.status === 'pending'
          }
        }));
      }
    } catch (error) {
      console.error('Follow toggle failed:', error);
    }
  };

  const handleFollowRequestAction = async (requestId, action) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`/api/users/me/requests/${requestId}/${action}`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        // Refresh follow requests
        fetchFollowRequests();
      }
    } catch (error) {
      console.error(`Failed to ${action} follow request:`, error);
    }
  };

  // Debounced search
  useEffect(() => {
    const timer = setTimeout(() => {
      handleSearch(searchQuery);
    }, 300);

    return () => clearTimeout(timer);
  }, [searchQuery]);

  const getFollowButtonContent = (searchUser) => {
    const status = followingStatus[searchUser._id];

    if (status?.isFollowing) {
      return {
        icon: <UserCheck size={16} />,
        text: 'Following',
        className: 'follow-btn following'
      };
    }

    if (status?.hasPendingRequest) {
      return {
        icon: <Clock size={16} />,
        text: 'Pending',
        className: 'follow-btn pending'
      };
    }

    return {
      icon: <UserPlus size={16} />,
      text: 'Follow',
      className: 'follow-btn'
    };
  };

  return (
    <div className="search-page">
      <div className="search-container">
        <h1>Discover Users</h1>

        <div className="search-box">
          <Search className="search-icon" size={20} />
          <input
            type="text"
            placeholder="Search for users by name or username..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="search-input"
          />
        </div>

        {/* Follow Requests Section */}
        {followRequests.length > 0 && (
          <div className="follow-requests-section">
            <h2>Follow Requests</h2>
            <div className="follow-requests">
              {followRequests.map((request) => (
                <div key={request.id} className="follow-request-card">
                  <div className="user-info">
                    <div className="user-avatar">
                      {request.user.avatar ? (
                        <img src={request.user.avatar} alt={request.user.username} />
                      ) : (
                        <div className="avatar-placeholder">
                          {request.user.username.charAt(0).toUpperCase()}
                        </div>
                      )}
                    </div>
                    <div className="user-details">
                      <h3>{request.user.fullName || request.user.username}</h3>
                      <p>@{request.user.username}</p>
                      <span className="request-time">
                        {new Date(request.requestedAt).toLocaleDateString()}
                      </span>
                    </div>
                  </div>
                  <div className="request-actions">
                    <button
                      onClick={() => handleFollowRequestAction(request.id, 'accept')}
                      className="accept-btn"
                    >
                      Accept
                    </button>
                    <button
                      onClick={() => handleFollowRequestAction(request.id, 'reject')}
                      className="reject-btn"
                    >
                      Decline
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Search Results */}
        <div className="search-results">
          {loading && <div className="loading">Searching...</div>}

          {!loading && searchQuery.trim() && searchResults.length === 0 && (
            <div className="no-results">
              <Search size={48} />
              <h3>No users found</h3>
              <p>Try searching with different keywords</p>
            </div>
          )}

          {searchResults.map((searchUser) => {
            const followButton = getFollowButtonContent(searchUser);

            return (
              <div key={searchUser._id} className="user-card">
                <div className="user-info">
                  <div className="user-avatar">
                    {searchUser.avatar ? (
                      <img src={searchUser.avatar} alt={searchUser.username} />
                    ) : (
                      <div className="avatar-placeholder">
                        {searchUser.username.charAt(0).toUpperCase()}
                      </div>
                    )}
                  </div>
                  <div className="user-details">
                    <h3>{searchUser.fullName || searchUser.username}</h3>
                    <p className="username">@{searchUser.username}</p>
                    <div className="user-stats">
                      <span className="stat">
                        <Users size={14} />
                        {searchUser.followerCount} followers
                      </span>
                      <span className="stat">
                        <FileText size={14} />
                        {searchUser.stats?.totalContent || 0} posts
                      </span>
                    </div>
                  </div>
                </div>
                <div className="user-actions">
                  <button
                    onClick={() => handleFollowToggle(searchUser.username, searchUser._id)}
                    className={followButton.className}
                  >
                    {followButton.icon}
                    {followButton.text}
                  </button>
                  <button 
                    onClick={() => window.open(`/u/${searchUser.username}`, '_blank')}
                    className="view-profile-btn"
                  >
                    <Eye size={16} />
                    View Profile
                  </button>
                </div>
              </div>
            );
          })}
        </div>

        {!searchQuery.trim() && searchResults.length === 0 && !loading && (
          <div className="search-placeholder">
            <Search size={64} />
            <h2>Find and Connect with Users</h2>
            <p>Search for users by their name or username to discover their content and connect with them.</p>
            <div className="search-tips">
              <h3>Tips:</h3>
              <ul>
                <li>Use at least 2 characters to search</li>
                <li>Search by username or full name</li>
                <li>Follow users to see their content in your feed</li>
                <li>Send follow requests to users with private profiles</li>
              </ul>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default SearchPage;
